var programUpdate = [];

var idProgram = "{!FieloEE__Program__c.Id}";
var resultProgram = sforce.connection.query("SELECT Id, {!$Setup.FieloEE__PublicSettings__c.FieloEE__PackagePrefix__c}Default__c FROM {!$Setup.FieloEE__PublicSettings__c.FieloEE__PackagePrefix__c}Program__c WHERE Id = \'{!FieloEE__Program__c.Id}\' ");

var program = resultProgram.getArray("records")[0];
program.{!$Setup.FieloEE__PublicSettings__c.FieloEE__PackagePrefix__c}Default__c = 'true';
programUpdate.push(program);

if(confirm('{!$Label.fieloee__programsetdefault}')){
  result = sforce.connection.update(programUpdate);
  parent.location.href = idProgram;
}
